-- ----------------------------
-- Table structure for np_user
-- ----------------------------
DROP TABLE IF EXISTS `np_user`;
CREATE TABLE `np_user`  (
  `user_id` int(11) NOT NULL AUTO_INCREMENT,
  `upwd` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `phone` varchar(16) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `avatar` varchar(60) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `user_name` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
